import React, { useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Sphere, Stars } from '@react-three/drei';
import * as THREE from 'three';
import "./App.css"

function SpinningGlobe() {
  const globeRef = useRef();

  // Rotate the globe continuously
  useFrame(() => {
    if (globeRef.current) {
      globeRef.current.rotation.y += 0.01; // Adjust speed here
    }
  });

  // Load texture maps
  const earthTexture = new THREE.TextureLoader().load('https://planetpixelemporium.com/downloads/earthmap1k.jpg');
  const bumpMap = new THREE.TextureLoader().load('https://planetpixelemporium.com/downloads/earthbump1k.jpg');
  const specularMap = new THREE.TextureLoader().load('https://planetpixelemporium.com/downloads/earthspec1k.jpg');

  return (
    <Sphere ref={globeRef} args={[2, 32, 32]} scale={1.5}>
      <meshStandardMaterial
        attach="material"
        map={earthTexture}             // Earth texture (color)
        bumpMap={bumpMap}              // Bump map for elevation
        bumpScale={0.05}               // Adjust bump scale to control elevation effect
        specularMap={specularMap}      // Specular map for water reflections
        metalness={0.5}                // Simulate reflective surfaces
        roughness={0.7}                // Control the roughness for a more realistic look
      />
    </Sphere>
  );
}

function App() {
  return (
    <Canvas camera={{ position: [0, 0, 5], fov: 50 }}>
      {/* Lighting */}
      <ambientLight intensity={0.5} />
      <directionalLight position={[5, 5, 5]} intensity={1} />
      {/* Stars in the background */}
      <Stars radius={100} depth={50} count={5000} factor={7} saturation={0} fade />
      {/* Orbit controls to rotate the scene */}
      <OrbitControls />
      {/* The spinning globe */}
      <SpinningGlobe />
    </Canvas>
  );
}

export default App;
