import React, { useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Sphere } from '@react-three/drei';
import * as THREE from 'three';

function SpinningGlobe() {
  const globeRef = useRef();

  // Rotate the globe continuously
  useFrame(() => {
    if (globeRef.current) {
      globeRef.current.rotation.y += 0.01; // Adjust speed here
    }
  });

  return (
    <Sphere ref={globeRef} args={[2, 32, 32]} scale={1.5}>
      <meshStandardMaterial
        attach="material"
        color="blue"
        wireframe={false}
        map={new THREE.TextureLoader().load('https://upload.wikimedia.org/wikipedia/commons/2/28/Simple_globe_map.png')}
      />
    </Sphere>
  );
}

function App() {
  return (
    <Canvas camera={{ position: [0, 0, 5], fov: 50 }}>
      <ambientLight intensity={0.5} />
      <directionalLight position={[5, 5, 5]} intensity={1} />
      <OrbitControls />
      <SpinningGlobe />
    </Canvas>
  );
}

export default App;
