import earthmap1k from './earthmap1k.jpg';
import earthbump1k from './earthbump1k.jpg';
import earthcloudmap from './earthcloudmap.jpg';
import earthcloudtexture1k from './earthcloudmaptrans.jpg';

import React, { useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Sphere, Stars } from '@react-three/drei';
import * as THREE from 'three';
import "./App.css";

function SpinningGlobe() {
  const globeRef = useRef();
  const cloudRef = useRef();

  useFrame(() => {
    if (globeRef.current) {
      globeRef.current.rotation.y += 0.01;
    }
    if (cloudRef.current) {
      cloudRef.current.rotation.y += 0.011; // Adjust cloud rotation speed
    }
  });

  const earthTexture = new THREE.TextureLoader().load(earthmap1k);
  const bumpMap = new THREE.TextureLoader().load(earthbump1k);
  const cloudTexture = new THREE.TextureLoader().load(earthcloudmap);

  return (
    <>
      <Sphere ref={globeRef} args={[2, 32, 32]} scale={1}>
        <meshStandardMaterial
          attach="material"
          map={earthTexture}
          bumpMap={bumpMap}
          bumpScale={0.05}
          metalness={0.5}
          roughness={0.7}
        />
      </Sphere>
      <Sphere ref={cloudRef} args={[2.01, 32, 32]} scale={1}>
        <meshBasicMaterial
          attach="material"
          map={cloudTexture}
          transparent
          opacity={0.2} // Adjust opacity to make clouds more transparent
        />
      </Sphere>
    </>
  );
}

function App() {
  return (
    <Canvas camera={{ position: [0, 0, 5], fov: 50 }}>
      {/* Lighting */}
      <ambientLight intensity={0.5} />
      <directionalLight position={[5, 5, 5]} intensity={1} />
      <directionalLight position={[-5, -5, -5]} intensity={0.5} />
      {/* Stars in the background */}
      <Stars radius={100} depth={50} count={5000} factor={7} saturation={0} fade />
      {/* Orbit controls to rotate the scene */}
      <OrbitControls />
      {/* The spinning globe */}
      <SpinningGlobe />
    </Canvas>
  );
}

export default App;
