import React, { useRef, useState } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Sphere, Stars } from '@react-three/drei';
import * as THREE from 'three';
import "./App.css";

function SpinningGlobe() {
  const globeRef = useRef();
  const [earthTexture, setEarthTexture] = useState(null);
  const [bumpMap, setBumpMap] = useState(null);

  useFrame(() => {
    if (globeRef.current) {
      globeRef.current.rotation.y += 0.01; // Adjust speed here
    }
  });

  // Load texture maps
  const loadTextures = () => {
    const textureLoader = new THREE.TextureLoader();

    textureLoader.load(
      'https://upload.wikimedia.org/wikipedia/commons/4/4c/Earth_political_1200.jpg',
      (texture) => {
        setEarthTexture(texture);
      },
      undefined,
      (error) => {
        console.error('Error loading texture:', error);
      }
    );

    textureLoader.load(
      'https://raw.githubusercontent.com/davidcech/earth-texture/master/earthbump1k.jpg',
      (texture) => {
        setBumpMap(texture);
      },
      undefined,
      (error) => {
        console.error('Error loading bump map:', error);
      }
    );
  };

  React.useEffect(() => {
    loadTextures();
  }, []);

  if (!earthTexture || !bumpMap) {
    return null; // Prevent rendering until textures are loaded
  }

  return (
    <Sphere ref={globeRef} args={[2, 32, 32]} scale={1}>
      <meshStandardMaterial
        attach="material"
        map={earthTexture}             // Earth texture (color)
        bumpMap={bumpMap}              // Bump map for elevation
        bumpScale={0.05}               // Adjust bump scale to control elevation effect
        metalness={0.5}                // Simulate reflective surfaces
        roughness={0.7}                // Control the roughness for a more realistic look
      />
    </Sphere>
  );
}

function App() {
  return (
    <Canvas camera={{ position: [0, 0, 5], fov: 50 }}>
      {/* Lighting */}
      <ambientLight intensity={0.5} />
      <directionalLight position={[5, 5, 5]} intensity={1} />
      <directionalLight position={[-5, -5, -5]} intensity={0.5} />
      {/* Stars in the background */}
      <Stars radius={100} depth={50} count={5000} factor={7} saturation={0} fade />
      {/* Orbit controls to rotate the scene */}
      <OrbitControls />
      {/* The spinning globe */}
      <SpinningGlobe />
    </Canvas>
  );
}

export default App;
