// App.js
import React, { useState } from 'react';
import { Canvas } from '@react-three/fiber';
import SpinningGlobe from './SpinningGlobe'; // Import the globe component
import IntroScreen from './component/IntroScreen'; // Import the intro screen component
import './App.css'; // Ensure your main CSS is imported

function App() {
  const [showGlobe, setShowGlobe] = useState(false);

  const handleProceed = () => {
    setShowGlobe(true);
  };

  return (
    <>
      {showGlobe ? (
        <Canvas camera={{ position: [0, 0, 5], fov: 50 }}>
          <ambientLight intensity={0.5} />
          <directionalLight position={[5, 5, 5]} intensity={1} />
          <directionalLight position={[-5, -5, -5]} intensity={0.5} />
          <SpinningGlobe />
        </Canvas>
      ) : (
        <IntroScreen onProceed={handleProceed} />
      )}
    </>
  );
}

export default App;
