import React, { useRef, useEffect, useState } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Sphere, Stars } from '@react-three/drei';
import * as THREE from 'three';
import ""

function SpinningGlobe() {
  const globeRef = useRef();
  const [textures, setTextures] = useState({});

  useEffect(() => {
    const textureLoader = new THREE.TextureLoader();
    const earthTexture = textureLoader.load('https://planetpixelemporium.com/downloads/earthmap1k.jpg');
    const bumpMap = textureLoader.load('https://planetpixelemporium.com/downloads/earthbump1k.jpg');
    const specularMap = textureLoader.load('https://planetpixelemporium.com/downloads/earthspec1k.jpg');

    setTextures({ earthTexture, bumpMap, specularMap });
  }, []);

  useFrame(() => {
    if (globeRef.current) {
      globeRef.current.rotation.y += 0.01; 
    }
  });

  // Wait until all textures are loaded
  if (!textures.earthTexture || !textures.bumpMap || !textures.specularMap) {
    return null; // Render nothing until textures are loaded
  }

  return (
    <Sphere ref={globeRef} args={[2, 64, 64]} scale={1.5}>
      <meshStandardMaterial
        attach="material"
        map={textures.earthTexture}
        bumpMap={textures.bumpMap}
        bumpScale={0.1}
        specularMap={textures.specularMap}
        metalness={0.2}
        roughness={0.5}
      />
    </Sphere>
  );
}

function App() {
  return (
    <Canvas camera={{ position: [0, 0, 5], fov: 50 }}>
      <ambientLight intensity={0.5} />
      <directionalLight position={[5, 5, 5]} intensity={1} />
      <Stars radius={100} depth={50} count={5000} factor={7} saturation={0} fade />
      <OrbitControls />
      <SpinningGlobe />
    </Canvas>
  );
}

export default App;
